mapper.update:
update questioninfo
set content = #{content,jdbcType=VARCHAR},
  `options` = #{options,jdbcType=VARCHAR},
  answer = #{answer,jdbcType=VARCHAR},
  `type` = #{type,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, content, `options`, answer, `type`
from questioninfo
where id = #{id,jdbcType=INTEGER}
